% !TeX spellcheck = en_US
\textbf{SPD Matrices}

{\color{navy}
	\textbf{Definition.} Let  $A\in\mathbb{R}^{n \times n}$ be a matrix.
	\begin{itemize}
		\item $A$ is called \textbf{symmetric}, if $A^\top = A$.
		\item $A$ is called \textbf{positive definite (semi-definite)}, if $x^\top Ax>0$ ($x^\top Ax\geq0$) for all $x\neq 0$.
	\end{itemize}
\textit{Remark:} If $A$ is symmetric and positive definite (SPD), then $(x,y) := x^T A y $ defines a general so-called \textit{inner product.} In the lecture we mainly consider the \textit{standard inner product} $$(\cdot,\cdot)_2\colon \mathbb{R}^n\times \mathbb{R}^n \to \mathbb{R},~ (x,y) \mapsto x^TI_ny=\sum_{i=1}^n x_i y_i$$ which corresponds to the symmetric and positive definite matrix $I_n$ (identity matrix).\\
}
~\\
\textbf{Tasks:}
\begin{enumerate}
	\item Find an example $A \in \mathbb{R}^{2 \times 2}$ which is symmetric and positive definite.
	\item Draw the set $\{ Ax\colon \|x\|_2 = 1\}$.
\end{enumerate}

solution:
{
\color{solution}
\begin{enumerate}
	\item  Let us consider $A:= \begin{bmatrix}a_{11}&a_{12}\\a_{21}&a_{22}\end{bmatrix} $ with $a_{12} = a_{21}$ and first analye the property for being $A$ being positive definite. We see
	\begin{align*}
		x^TAx=a_{11}x_1^2+a_{12}x_1x_2+a_{12}x_2x_1+a_{22}x_2^2
		=a_{11}x_1^2+2a_{12}x_1x_2+a_{22}x_2^2
		\stackrel{!}{>} 0\ \ \forall x=(x_1,x_2)^\top\neq (0,0)^\top.
		\end{align*}
	\underline{Example:}
	\begin{align*}
		A=\begin{bmatrix}2&1\\1&2\end{bmatrix},~ x^TAx &=\overbrace{2x_1^2}^{\textcolor{blue}{x_1^2+x_1^2}}+2x_1x_2+2x_2^2\\
		&=x_1^2+x_2^2+\underbrace{(x_1+x_2)^2}_{\geq0}\\
		&\geq x_1^2+x_2^2\\
		&>0~~~~~~~~~ \forall\begin{pmatrix}x_1\\x_2\end{pmatrix}\neq\begin{pmatrix}0\\0\end{pmatrix}
		\end{align*}
	\item  Consider
	$\{Ax:\|{x}\|_2 =1\}$ with $\ A=\begin{bmatrix}2&1\\1&2\end{bmatrix}$. Also recall from the lecture that $\left(3, \begin{bmatrix}1\\1\end{bmatrix}\right), \left(1, \begin{bmatrix}1\\-1\end{bmatrix}\right)$ are eigenpairs (see later).
	\begin{center}
		\includegraphics[width=0.6\textwidth]{spdMatrix.pdf}
	\end{center}
	~\\Further general note:
	\begin{center}
		\includegraphics[width=0.99\linewidth]{../../rep/exercises/direct-solver/ex-spdMatsAndNorms}
	\end{center}

\end{enumerate}
}
