% !TeX spellcheck = de_AT_frami
!!!Separate Aufgaben zu: int und float!!!
\begin{enumerate}
	\item Berechnen Sie die folgenden Summen im entsprechenden Stellenwertsystem.
		\begin{enumerate}
			\item  $(10101010)_2$ + $(11100000)_2$
			\item $(23)_7$ + $(17)_7$
			\item $(19)_{h}$ + $(12)_{h}$ (h für \textit{hexadezimal})
		\end{enumerate}
	!! Ziffernvorrat angeben !! ((b) ist ungültig)
	\item 
	\begin{enumerate}
		\item Wandeln Sie die folgenden Zweierkomplementzahlen ins Dezimalsystem um.
		\begin{enumerate}
			\item $(10101010)_{\Z}$
			\item $(11100000)_{\Z}$
		\end{enumerate}
		\item Hierbei handelt es sich um die Bitfolgen von oben. Angenommen Sie betrachten nur die ersten 8 Bits des Ergebnisses der Summe (d.h. das höchstwertigste Bit wird ggf. abgeschnitten). Bestimmen Sie das letzte Übertragsbit und das Überlaufsbit.  Treffen Sie damit Aussagen über die Gültigkeit des abgeschnittenen Ergebnisses: Interpretieren Sie dabei einmal alle Bitfolgen als Zweierkomplementzahlen und einmal alle als vorzeichenlose Binärzahlen.
	\end{enumerate}
	\item Stellen Sie die Zahlen $x = 7$ und $z = -7$ als Zweierkomplementzahl der Länge $N = 4$ sowie $N = 6$ dar.
	\item Betrachten Sie die binären Gleitkommazahlen gemäß der Parameter \texttt{s2e2}.
	\begin{enumerate}
		\item Ermitteln Sie den Verschiebewert B (BIAS) für den Exponenten gemäß des IEEE-754 Standards, sowie die Grenzen $e_{\text{min}}$ und $e_{\text{max}}$ des verschobenen Exponentenwertebereichs  (berücksichtigen Sie, dass bestimmte Bitmuster für spezielle Werte reserviert sind).
		\item Mit vielen Bits wird eine Gleitkommazahl hier gespeichert? Erklären Sie die Bedeutung der einzelnen Bits und wie daraus die Gleitkommazahl gebildet wird.
	\end{enumerate}
\end{enumerate}

solution:
% !TeX spellcheck = de_AT_frami
\begin{enumerate}
	\item Berechnen Sie die folgenden Summen im entsprechenden Stellenwertsystem.
	\begin{enumerate}
		\item \textcolor{red}{(1P)} $(10101010)_2$ (170) + $(11100000)_2$ (224) = $(110001010)_2$  (394)\\
		hier: $C=\texttt{carry(N-1,N)}=1,~~~ c=\texttt{carry(N-2,N-1)}=1$
		\item \textcolor{red}{(1P)} Ungültige Ziffer 7 \\
		Falsch:$(23)_7$ (17) + $(17)_7$ (14) = $(43)_7$ (31)
		\item \textcolor{red}{(1P)} $(19)_{h}$  (25) + $(12)_{h}$ (18) = $(2B)_{h}$ (43)
	\end{enumerate}
	\item 
	\begin{enumerate}
		\item Wandeln Sie die folgenden 2er-Komplementzahlen ins Dezimalsystem um.
		\begin{enumerate}
			\item \textcolor{red}{(0.5P)} $(10101010)_{\Z}$ = -128 +42 = -86
			\item \textcolor{red}{(0.5P)} $(11100000)_{\Z}$ = -128 + 96 = -32
		\end{enumerate}
		\item \textcolor{red}{(0.5P)} Übertragsbit = 1  ~~~~~~~~~ \textcolor{red}{(1P)} $\rightarrow$ Ergebnis als vorzeichenlose Zahl ungültig \\
		\textcolor{red}{(0.5P)} Überlaufsbit = $C \oplus c = 1 \oplus 1$ = 0~~~~~~~~ \textcolor{red}{(1P)} $\rightarrow$ Ergebnis als Zweierkomplementzahl gültig 
	\end{enumerate}
	\item \textcolor{red}{(1P)} $x = 7 = (0111)_Z = (000111)_Z$\\
	\textcolor{red}{(1P)} $z = -7 = (1001)_Z = (111001)_Z$
	\item Betrachten Sie die binären Gleitkommazahlen gemäß der Parameter \texttt{s2e2}.
	\begin{enumerate}
		\item  Länge für Exponentenbitfolge = 2, also  $B = 2^{2-1}-1 = 1$\\
		mögliche Bitmuster für Exponent: 00, 11 (reserviert für subnormals und NaN), 10 (=2), 01 (=1)\\
		Also \textcolor{red}{(0.5P)} $e_{\text{min}} = (01)_2 - B = 1- 1 = 0$\\
		und \textcolor{red}{(0.5P)} $e_{\text{max}}= (01)_2 - B = 2- 1 = 1$ 
		\item \textcolor{red}{(1P)} 5 Bits: 1 Vorzeichen, 2 Exponent, 2 Mantisse \\ in der Reihenfolge: \texttt{VEEMM}
	\end{enumerate}
\end{enumerate}
